
module.exports = class Print {
  static length = 0;

  static line = function (text) {
    if (this.length !== 0) {
      process.stdout.write('\r' + ' '.repeat(this.length) + '\r');
    } 
    process.stdout.write(text);
    this.length = text.length;
  };

  static newLine = function (text = '') {
    if (this.length !== 0) {
      if (!text) {
        process.stdout.write('\n');
      } else {
        process.stdout.write('\n' + text + '\n');
      }
    } else {
      process.stdout.write(text + '\n');
    }
  }
}
